mapper.update:
UPDATE WN_DSP_WORK_INFO

    OBJ_ID = #{objId},
    SITE_ID = #{siteId},
    DSP_WORK_ID = #{dspWorkId},
    EQP_ID = #{eqpId},
    PORT_ID = #{portId},
    LOT_ID = #{lotId},
    TRACK_IN_CNFM_YN = #{trackInCnfmYn},
    EQP_CHK_YN = #{eqpChkYn},
    RCP_CHK_YN = #{rcpChkYn},
    RCP_ID = #{rcpId},
    DSP_STAT_CD = #{dspStatCd},
    EVNT_NM = #{evntNm},
    PREV_EVNT_NM = #{prevEvntNm},
    CSTM_EVNT_NM = #{cstmEvntNm},
    PREV_CSTM_EVNT_NM = #{prevCstmEvntNm},
    USE_STAT_CD = #{useStatCd},
    RSN_CD = #{rsnCd},
    TRNS_CM = #{trnsCm},
    CRT_USER_ID = #{crtUserId},
    CRT_DT = #{crtDt},
    MDFY_USER_ID = #{mdfyUserId},
    MDFY_DT = #{mdfyDt},
    FNL_EVNT_DT = #{fnlEvntDt},
    TID = #{tid},


    AND OBJ_ID = #{pObjId}
    AND SITE_ID = #{pSiteId}
    AND DSP_WORK_ID = #{pDspWorkId}
    AND EQP_ID = #{pEqpId}
    AND PORT_ID = #{pPortId}
    AND LOT_ID = #{pLotId}


    SELECT OBJ_ID AS objId
    FROM WN_DSP_WORK_INFO

        AND OBJ_ID = #{pObjId}
        AND SITE_ID = #{pSiteId}
        AND DSP_WORK_ID = #{pDspWorkId}
        AND EQP_ID = #{pEqpId}
        AND PORT_ID = #{pPortId}
        AND LOT_ID = #{pLotId}
