mapper.update:
UPDATE WN_TRANSFER_JOB
CARR_ID = #{carrId}, CRNT_EQP_ID = #{crntEqpId}, CRNT_PORT_ID = #{crntPortId}, SRC_EQP_ID = #{srcEqpId}, SRC_PORT_ID = #{srcPortId}, DEST_EQP_ID = #{destEqpId}, DEST_PORT_ID = #{destPortId}, MOVE_STAT_CD = #{moveStatCd}, PRIRT_NO = #{prirtNo}, FNL_EVNT_NM = #{fnlEvntNm}, FNL_EVNT_DT = SYSDATE, MDFY_USER_ID = #{mdfyUserId}, MDFY_DT = SYSDATE, EVNT_NM = #{evntNm}, PREV_EVNT_NM = EVNT_NM, CSTM_EVNT_NM = #{cstmEvntNm}, PREV_CSTM_EVNT_NM = CSTM_EVNT_NM, RSN_CD = #{rsnCd}, TID = #{tid}, TRNS_CM = #{trnsCm}, USE_STAT_CD = #{useStatCd},
AND SITE_ID = #{pSiteId} AND JOB_ID = #{pJobId} AND CARR_ID = #{pCarrId} AND MOVE_STAT_CD = #{pMoveStatCd} AND USE_STAT_CD = #{pUseStatCd}
SELECT OBJ_ID as objId FROM WN_TRANSFER_JOB
AND SITE_ID = #{pSiteId} AND JOB_ID = #{pJobId} AND CARR_ID = #{pCarrId} AND MOVE_STAT_CD = #{pMoveStatCd} AND USE_STAT_CD = #{pUseStatCd}

mapper.insert:
