,EVNT_NM
,PREV_CSTM_EVNT_NM
,PREV_EVNT_NM
,RSN_CD
,TID
,TRNS_CM
,USE_STAT_CD
)
VALUES (
#{objId}
,#{siteId}
,#{jobId}
,#{carrId}
,#{crntEqpId}
,#{crntPortId}
,#{srcEqpId}
,#{srcPortId}
,#{destEqpId}
,#{destPortId}
,#{moveStatCd}
