,#{prevCstmEvntNm}
,#{prevEvntNm}
,#{rsnCd}
,#{tid}
,#{trnsCm}
,#{useStatCd}

)

    SELECT TO_CHAR(CURRENT_TIMESTAMP,'YYYYMMDDHH24MISSFF6') || '-' || SYS_GUID() as objId FROM DUAL

mapper.delete:
DELETE FROM WN_TRANSFER_JOB
WHERE SITE_ID = #{pSiteId}
AND JOB_ID = #{pJobId}
