mapper.update:
UPDATE WN_WORK_STAT

    EQP_ID = #{eqpId},
    WORK_STAT_CD = #{workStatCd},
    WORK_START_TM = SYSDATE,
    WORK_QTY = #{workQty},
    FNL_TASK_NM = #{fnlTaskNm},
    BATCH_YN = #{batchYn},
    INLINE_YN = #{inlineYn},
    EVNT_NM = #{evntNm},
    PREV_EVNT_NM = EVNT_NM,
    CSTM_EVNT_NM = #{cstmEvntNm},
    PREV_CSTM_EVNT_NM = CSTM_EVNT_NM,
    USE_STAT_CD = #{useStatCd},
    RSN_CD = #{rsnCd},
    TRNS_CM = #{trnsCm},
    MDFY_USER_ID = #{mdfyUserId},
    MDFY_DT = SYSDATE,
    FNL_EVNT_DT = SYSDATE,
    TID = #{tid},
    EQP_INLINE_ID = #{eqpInlineId},
    INLINE_STAT_CD = #{inlineStatCd},
    DSP_WORK_ID = #{dspWorkId},


    AND OBJ_ID = #{pObjId}
    AND SITE_ID = #{pSiteId}
    AND WORK_ID = #{pWorkId}
    AND EQP_ID = #{pEqpId}
    AND WORK_STAT_CD = #{pWorkStatCd}
    AND USE_STAT_CD = #{pUseStatCd}


    SELECT OBJ_ID AS objId
    FROM WN_WORK_STAT

        AND OBJ_ID = #{pObjId}
        AND SITE_ID = #{pSiteId}
        AND WORK_ID = #{pWorkId}
        AND EQP_ID = #{pEqpId}
        AND WORK_STAT_CD = #{pWorkStatCd}
        AND USE_STAT_CD = #{pUseStatCd}

mapper.insert:
