mapper.update:
UPDATE WN_WORK_JOB_SLOT_INFO
PROD_MTRL_STRT_TM = SYSDATE, PROD_MTRL_END_TM = SYSDATE, JOB_STAT_CD = #{jobStatCd}, SELF_INSP_YN = #{selfInspYn}, MTRL_FACE_CD = #{mtrlFaceCd}, EVNT_NM = #{evntNm}, PREV_EVNT_NM = EVNT_NM, CSTM_EVNT_NM = #{cstmEvntNm}, PREV_CSTM_EVNT_NM = CSTM_EVNT_NM, USE_STAT_CD = #{useStatCd}, RSN_CD = #{rsnCd}, TRNS_CM = #{trnsCm}, MDFY_USER_ID = #{mdfyUserId}, MDFY_DT = SYSDATE, FNL_EVNT_DT = SYSDATE, TID = #{tid}, LOT_ID = #{lotId},
AND OBJ_ID = #{pObjId} AND SITE_ID = #{pSiteId} AND WORK_ID = #{pWorkId} AND JOB_SEQ_ID = #{pJobSeqId} AND SLOT_NO = #{pSlotNo} AND PROD_MTRL_ID = #{pProdMtrlId} AND USE_STAT_CD = #{pUseStatCd}

mapper.insert:
INSERT INTO WN_WORK_JOB_SLOT_INFO (
OBJ_ID
,SITE_ID
,WORK_ID
,JOB_SEQ_ID
,SLOT_NO
,PROD_MTRL_ID
,PROD_MTRL_STRT_TM
,PROD_MTRL_END_TM
,JOB_STAT_CD
,SELF_INSP_YN
,MTRL_FACE_CD
,EVNT_NM
,PREV_EVNT_NM
,CSTM_EVNT_NM
,PREV_CSTM_EVNT_NM
,USE_STAT_CD
,RSN_CD
,TRNS_CM
,CRT_USER_ID
,CRT_DT
,MDFY_USER_ID
,MDFY_DT
,FNL_EVNT_DT
,TID
,LOT_ID
)
VALUES (
#{objId}
,#{siteId}
,#{workId}
,#{jobSeqId}
,#{slotNo}
,#{prodMtrlId}
,#{prodMtrlStrtTm}
,#{prodMtrlEndTm}
,#{jobStatCd}
,#{selfInspYn}
,#{mtrlFaceCd}
,#{evntNm}
,#{prevEvntNm}
,#{cstmEvntNm}
,#{prevCstmEvntNm}
,#{useStatCd}
,#{rsnCd}
,#{trnsCm}
,#{crtUserId}
,SYSDATE
,#{mdfyUserId}
,SYSDATE
,SYSDATE
,#{tid}
,#{lotId}
)

    SELECT TO_CHAR(CURRENT_TIMESTAMP,'YYYYMMDDHH24MISSFF6') || '-' || SYS_GUID() as objId FROM DUAL

mapper.delete:
DELETE FROM WN_WORK_JOB_SLOT_INFO
WHERE SITE_ID = #{pSiteId}
  AND WORK_ID = #{pWorkId}
  AND JOB_SEQ_ID = #{pJobSeqId}
